mapper.insert:
INSERT INTO t_user_daily_sign_in (
        user_id, month, detail, continuous_num, total_num
) VALUES (
        #{userId}, #{month}, #{detail}, #{continuousNum}, #{totalNum}
)

mapper.select:
SELECT
        user_id, `month`, detail, total_num, continuous_num
FROM t_user_daily_sign_in
WHERE 1 = 1
AND   `user_id` = #{userId}
AND   `month` = #{month}
