mapper.update:
UPDATE t_user_flow
SET
        current_approver  = #{currentApprover}
WHERE   delFlag = 0
AND     user_id = #{userId}
AND     flow_id = #{flowId}

mapper.select:
SELECT `user_id` AS `userId`, `flow_id` AS `flowId`, `flow_type` AS `flowType`, `current_approver` AS `currentApprover`, `remark` AS `remark`, `create_user` AS `createUser`, `create_time` AS `createTime`, `update_user` AS `updateUser`, `update_time` AS `updateTime` FROM t_user_flow WHERE 1 = 1
AND user_id = #{userId}
AND flow_id = #{flowId}
AND flow_type = #{flowType}
AND current_approver = #{currentApprover}
# 从 第offset条记录开始(不包含offset)，返回pageSize条记录。例如：LIMIT 5,10; // 检索记录行 6-15 # 换句话说，LIMIT n 等价于 LIMIT 0,n。 LIMIT #{offset}, #{pageSize}

mapper.delete:
DELETE FROM t_user_flow
WHERE   delFlag != 0
AND     user_id = #{userId}
AND     flow_id = #{flowId}
